CREATE TABLE actors(
  adult TINYINT,
  gender TINYINT,
  id SMALLINT PRIMARY KEY,
  know_for_department VARCHAR(15),
  name VARCHAR(25),
  original_name VARCHAR(25),
  popularity DECIMAL(6, 3),
  profile_path VARCHAR(255),
  cast_id TINYINT,
  character_name VARCHAR(30),
  credit_id VARCHAR(30),
  order_type TINYINT,
  biography TEXT,
  birthday VARCHAR(10),
  deathday VARCHAR(10),
  homepage VARCHAR(255),
  imbd_id VARCHAR(15),
  place_of_birth VARCHAR(30),
  moviesId JSON
);

CREATE TABLE users(
  id TINYINT PRIMARY KEY AUTO_INCREMENT,
  name VARCHAR(15),
  email VARCHAR(50),
  password VARCHAR(50)
);

CREATE TABLE comments(
  id TINYINT PRIMARY KEY AUTO_INCREMENT,
  message TEXT,
  createdAt BIGINT,
  moviesId MEDIUMINT,
  userId TINYINT
);

